import React, { useState } from "react";
import logo from "./logos/FULL_LOGO_COLOR.png";
import Navbar from "react-bootstrap/Navbar";
import { Form } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import { Container } from "react-bootstrap";
import { Popover, ArrowContainer } from "react-tiny-popover";

const TopNavBar = () => {
  const [isPopoverOpen, setIsPopoverOpen] = useState(false);
  return (
    <div>
      <Navbar
        bg="dark"
        data-bs-theme="dark"
        sticky="top"
        className="bg-body-tertiary"
      >
        <Container>
          <Form inline>
            <Form.Control
              placeholder="Search Videos..."
              aria-label="Search Videos..."
              aria-describedby="basic-addon1"
            ></Form.Control>
          </Form>
          <Form inline>
            <Navbar.Brand href="#home">
              <img
                width={150}
                height={40}
                src={logo}
                className="d-inline-block align-top"
              />
            </Navbar.Brand>
          </Form>
          <Form inline>
            <Popover
              isOpen={isPopoverOpen}
              positions={["top", "left"]} // if you'd like, you can limit the positions
              padding={10} // adjust padding here!
              reposition={false} // prevents automatic readjustment of content position that keeps your popover content within its parent's bounds
              onClickOutside={() => setIsPopoverOpen(false)}
              content={(
                { position, nudgedLeft, nudgedTop } // you can also provide a render function that injects some useful stuff!
              ) => (
                <div>
                  <div>
                    Hi! I'm popover content. Here's my current position:{" "}
                    {position}.
                  </div>
                  <div>
                    I'm {` ${nudgedLeft} `} pixels beyond my boundary
                    horizontally!
                  </div>
                  <div>
                    I'm {` ${nudgedTop} `} pixels beyond my boundary vertically!
                  </div>
                </div>
              )}
            >
              <div>
            <Button onClick={() => setIsPopoverOpen(!isPopoverOpen)} type="upload">Upload</Button>
            </div>
            </Popover>
          </Form>
        </Container>
      </Navbar>
    </div>
  );
};

export default TopNavBar;
