import React from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

const GET_URL_API = "https://take-home-assessment-423502.uc.r.appspot.com/api/videos";


export class Popup extends React.Component {
  constructor(props) {
    super(props);
    this.setState({
        title: '',
        videoLink:''
    });
    this.handleLinkChange = this.handleLinkChange.bind(this);
    this.handleTitleChange = this.handleTitleChange.bind(this);
  }

  handleTitleChange (contents) {
    console.log("Contents: " + contents);
    this.setState({
        title: contents.target[0].value
    });
  }

  handleLinkChange(contents) {
    this.setState({
        videoLink: contents.target[0].value
    });
  }

  render() {
    return (
      <div style={{ backgroundColor: "white" }}>
        <h3>Upload a Video</h3>
        <Form>
          <Form.Control
            type="title"
            placeholder="Title your video"
            className="title"
            value={this.state.title}
            onChange={this.handleTitleChange}
            aria-label="Title"
          />
          <Form.Control
            type="link"
            placeholder="https://your-video-link.com"
            value={this.state.videoLink}
            onChange={this.handleLinkChange}
            className="link"
            aria-label="Link"
          />
        </Form>
        <Button onClick={() => this.props.setIsPopoverOpen(!this.props.isPopoverOpen)}>Cancel</Button>
        <Button onClick={() => {
            // Call POST create video
            console.log("Title: " + this.state.title)
            fetch(GET_URL_API, {
                method: 'POST',
                body: JSON.stringify({
                    user_id: "garret_tullio",
                    description: "Cool video",
                    video_url: "",
                    title: ""
                }) 
            })

        }}>Upload</Button>
      </div>
    );
  }
}
